import React, { useCallback, useEffect, useState } from "react";
import ROSLIB, { Ros } from "roslib";
import logo from "./logo.svg";
import "./App.css";

function App() {
  const [ros, setRos] = useState<Ros>();

  useEffect(() => {
    const _ros = new ROSLIB.Ros({
      url: "ws://localhost:9090",
    });
    console.log("API called");

    _ros.on("connection", () => {
      setRos(ros);
      console.log("Connected to ROSBridge WebSocket server.");
    });

    _ros.on("error", function (error: any) {
      console.log("Error connecting to ROSBridge WebSocket server: ", error);
    });

    _ros.on("close", function () {
      console.log("Connection to ROSBridge WebSocket server closed.");
    });

    // return () => {
    //   _ros.close();
    // };
  }, []);

  const publishCmd = useCallback(() => {
    if (!ros) {
      console.log("Not connected to ROSBridge WebSocket server.");
      return;
    }
    const cmdVel = new ROSLIB.Topic({
      ros,
      name: "/turtle1/cmd_vel",
      messageType: "geometry_msgs/Twist",
    });
    const twist = new ROSLIB.Message({
      linear: {
        x: 2.0,
        y: 0,
        z: 0,
      },
      angular: {
        x: 0,
        y: 0,
        z: 0,
      },
    });
    cmdVel.publish(twist);
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.tsx</code> and save to reload.
        </p>
        <button onClick={publishCmd}>Publish Cmd</button>
      </header>
    </div>
  );
}

export default App;
